mapper.select:
select

from account


      and name like CONCAT('%',#{name,jdbcType=VARCHAR},'%')


      and balance >= #{balanceStart,jdbcType=DECIMAL}


      and balance <= #{balanceEnd,jdbcType=DECIMAL}


      and open_time >= #{openTimeStart,jdbcType=TIMESTAMP}


      and open_time <= #{openTimeEnd,jdbcType=TIMESTAMP}
